package Chap03;

public class Example05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		System.out.println(x++);// x = 10 그리고 x = 11이 됨
		System.out.println(++x);// 바로 x = 12
		System.out.println(x--);// x = 12 그리고 x = 11이 됨
		System.out.println(--x);// x = 10이 됨
		
		
	}

}
