package Chap03;

public class Operator04 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		int b = 20;
		int c ;
		System.out.println(c = a); // a = 10 , c = 10
		System.out.println(b += a);// b = 30
		System.out.println(b -= a);// b = 30-10 = 20
		System.out.println(b *= a);// b = 20*10 = 200
		System.out.println(b /= a);// b = 200/10 = 20
		System.out.println(b %= a);// b = 20%10 = 0
	
	}

}
